import React, { useState } from "react";
import axios from "axios";

function CheckoutSmallFruits() {
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    email: "",
    phone: "",
    address: "",
    address2: "",
    state: "",
    city: "",
    zip: "",
    deliveryTime: "",
    frequency: "",
    boxType: ""
  });
  const {
    firstName,
    lastName,
    email,
    phone,
    address,
    address2,
    state,
    city,
    zip,
    deliveryTime,
    frequency,
    boxType
  } = formData;
  const onChange = e =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  // this.setState({
  //   firstName: firstName,
  //   lastName: lastName,
  //   email: email,
  //   phone: phone,
  //   address: address,
  //   address2: address2,
  //   state: state,
  //   city: city,
  //   zip: zip,
  //   deliveryTime: deliveryTime,
  //   frequency: frequency,
  //   boxType: boxType
  // })

  const onSubmit = async e => {
    e.preventDefault();
    console.log(formData);
  };

  return (
    <div>
      <div className="container">
        <div className="py-5 text-center mt-100 ">
          <h2>Checkout</h2>
        </div>

        <div className="row">
          <div className="col-md-4 order-md-2 mb-4">
            <h4 className="d-flex justify-content-between align-items-center mb-3">
              <span className="text-muted">Your total</span>
            </h4>
            <ul className="list-group mb-3">
              <li className="list-group-item d-flex justify-content-between lh-condensed">
                <div>
                  <h6 className="my-0">Freshbox Small Fruits box</h6>
                  <small className="text-muted">Includes FREE delivery</small>
                </div>
                <span className="text-muted">$14.99</span>
              </li>
            </ul>
          </div>
          <div className="col-md-8 order-md-1">
            <h4 className="mb-3">Delivery Information</h4>
            <form
              className="needs-validation"
              noValidate
              onSubmit={e => onSubmit(e)}
            >
              <div className="mb-3">
                <input
                  type="text"
                  readOnly
                  className="form-control-plaintext"
                  id="staticEmail"
                  value="Small Fruits"
                  name="boxType"
                />
              </div>
              <div className="row">
                <div className="col-md-6 mb-3">
                  <label htmlFor="firstName">First name</label>
                  <input
                    type="text"
                    className="form-control"
                    id="firstName"
                    placeholder=""
                    value={firstName}
                    name="firstName"
                    onChange={e => onChange(e)}
                    required
                  />
                  <div className="invalid-feedback">
                    Valid first name is required.
                  </div>
                </div>
