                    <linearGradient
                      x1="87.336%"
                      y1="7.447%"
                      x2="66.279%"
                      y2="44.56%"
                      id="hero-shapes-a"
                    >
                      <stop stopColor="#A3A9F1" offset="0%" />
                      <stop stopColor="#6A72E4" offset="100%" />
                    </linearGradient>
                    <linearGradient
                      x1="50%"
                      y1="0%"
                      x2="50%"
                      y2="100%"
                      id="hero-shapes-b"
