import React, { Component } from "react";
import FormProduct from "./FormProduct";
import FormPersonalDetails from "./FormPersonalDetails";
import ConfirmForm from "./ConfirmForm";
import PaymentPage from "./PaymentPage";

class CheckoutForm extends Component {
  state = {
    step: 1,
    produce: "",
    firstName: "",
    lastName: "",
    address: "",
    mobile: "",
    deliveryTime: ""
  };
  // Proceed to next step
  nextStep = () => {
    const { step } = this.state;
    this.setState({
      step: step + 1
    });
  };

  // Go back
  prevStep = () => {
    const { step } = this.state;
    this.setState({
      step: step - 1
    });
  };

  handleChange = input => e => {
    this.setState({
      [input]: e.target.value
    });
  };

  render() {
    const { step } = this.state;
    const {
      firstName,
      lastName,
      mobile,
      address,
      deliveryTime,
      produce
    } = this.state;

    const values = {
      firstName,
      lastName,
      mobile,
      address,
      deliveryTime,
      produce
    };
    switch (step) {
      case 1:
        return (
          <FormPersonalDetails
            nextStep={this.nextStep}
            handleChange={this.handleChange}
            values={values}
          />
        );
      case 2:
        return (
          <FormProduct
            nextStep={this.nextStep}
            prevStep={this.prevStep}
            handleChange={this.handleChange}
            values={values}
          />
        );

      case 3:
        return (
          <ConfirmForm
            nextStep={this.nextStep}
            prevStep={this.prevStep}
            values={values}
          />
        );
      case 4:
        return <PaymentPage />;
    }
  }
}
export default CheckoutForm;
