import React from "react";
import { Card, Accordion } from "react-bootstrap";

export default function HeroSection() {
  return (
    <div>
      <section className="hero">
        <div className="container">
          <div className="hero-inner">
            <div className="hero-copy">
              <h1 className="hero-title mt-0">FreshBox</h1>
              <p className="hero-paragraph">
                Weekly fruits and vegetables delivered Fresh to your home or
                office
              </p>
              <p className="hero-cta">
                <a className="button button-primary button-shadow" href="#">
                  Pricing and plans
                </a>
              </p>
            </div>
            <div className="hero-illustration">
              <div className="hero-shapes is-revealing">
                <svg
                  width="724"
                  height="672"
                  viewBox="0 0 724 672"
                  xmlns="http://www.w3.org/2000/svg"
                >
                  <defs>
                    <linearGradient
                      x1="87.336%"
                      y1="7.447%"
                      x2="66.279%"
                      y2="44.56%"
                      id="hero-shapes-a"
                    >
                      <stop stopColor="#00FF7F" offset="0%" />
                      <stop stopColor="#90EE90" offset="100%" />
                    </linearGradient>
                    <linearGradient
                      x1="50%"
                      y1="0%"
                      x2="50%"
                      y2="100%"
                      id="hero-shapes-b"
                    >
                      <stop stopColor="#fff" offset="0%" />
                      <stop stopColor="#98FB98" offset="100%" />
                    </linearGradient>
                  </defs>
                </svg>
              </div>
              <div
                className="hero-media is-revealing"
                style={{ perspective: "800px" }}
              >
                <Card.Img variant="top" src="/dist/images/box.png" />

